package com;

public class MatrixPattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// TODO Auto-generated method stub
				for(int i=1;i<=5;i++) {
					for(int j=1;j<=5;j++) {
						if(i==j) {
							System.out.print(i+" ");
						}
						else {
							System.out.print("* ");
						}
					}
					System.out.println();
				}


	}

}
